@Service
public class MetadataServiceImpl extends BaseServiceImpl implements MetadataService {
    @Autowired
    private MetadataMapper metadataMapper;
    // 返回你的mapper
    @Override
    protected BaseMapper getMapper(){return metadataMapper;}
}

